import React from 'react'
import styled from 'styled-components'
import Navbar from '../components/Navbar'
import Announcement from '../components/Announcement'
import Footer from '../components/Footer'
import { Add, Remove } from '@mui/icons-material'
import { mobile } from '../responseive'
import { useSelector } from 'react-redux'

const Container = styled.div``

const Wrapper = styled.h1`
  padding: 20px;
  ${mobile({ padding: '10px' })}
`

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  border: 2px solid #ce46e2;
  cursor: pointer;
  border: ${(props) => props.type === 'filled' && 'none'};
  background-color: ${(props) =>
    props.type === 'filled' ? 'wheat' : 'transparent'};
  color: ${(props) => props.type === 'filled' && 'black'};
`

const TopTexts = styled.div`
  ${mobile({ display: 'none' })}
`

const TopText = styled.span`
  text-decoration: underline;
  font-size: 25px;
  cursor: pointer;
  margin: 0px 10px;
`

const Buttom = styled.div`
  display: flex;
  justify-content: space-between;
  ${mobile({ flexDirection: 'column' })}
`

const Info = styled.div`
  flex: 3;
`
const Product = styled.div`
  display: flex;
  margin-bottom: 15px;
  justify-content: space-between;
  ${mobile({ flexDirection: 'column' })}
`

const ProductDetails = styled.div`
  flex: 3;
  display: flex;
  ${mobile({ flexDirection: 'column' })}
`

const Image = styled.img`
  height: 50%;
  width: 50%;
  object-fit: contain;
`

const Details = styled.div`
  padding: 15px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  ${mobile({ padding: '10px', margin: '10px 0px 5px 0px' })}
`

const ProductName = styled.span`
  font-weight: 300;
`

const ProductId = styled.span`
  font-weight: 300;
`

const ProductColor = styled.span`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`

const ProductSize = styled.span`
  font-weight: 300;
`

const PriceDetails = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`
const ProductAmount = styled.div`
  margin: 15px;
  font-weight: 300;
  ${mobile({ margin: '5px 15px' })}
`
const ProductPrice = styled.div`
  font-weight: 300;
  ${mobile({ marginBottom: '20px' })}
`

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
  margin-bottom: 15px;
  margin-right: 15px;
`

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 50vh;
  background-color: whitesmoke;
`
const SummaryTitle = styled.h1`
  font-weight: 200;
  font-size: 50px;
`
const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === 'total' && '800'};
  font-size: ${(props) => props.type === 'total' && '40px'};
`
const SummaryItemText = styled.span`
  font-weight: 200;
`
const SummaryItemPrice = styled.span`
  font-weight: 200;
`
const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: wheat;
  color: black;
  font-weight: 600;
  border: none;
  cursor: pointer;
  margin-top: 25px;
`
const Cart = () => {
  const cart = useSelector((state) => state.cart)
  return (
    <Container>
      <Announcement />
      <Navbar />
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag(2)</TopText>
            <TopText>WishList(0)</TopText>
          </TopTexts>
          <TopButton type='filled'>CHECKOUT NOW!</TopButton>
        </Top>
        <Buttom>
          <Info>
            {cart.products.map((product) => (
              <Product>
                <ProductDetails>
                  <Image src={product.image} />
                  <Details>
                    <ProductName>
                      <b>Product:</b> {product.title}
                    </ProductName>
                    <ProductId>
                      <b>ID:</b> {product._id}
                    </ProductId>
                    <ProductColor color={product.color} />
                    <ProductSize>
                      <b>Size:</b> {product.size}
                    </ProductSize>
                  </Details>
                </ProductDetails>
                <PriceDetails>
                  <ProductAmountContainer>
                    <Add />
                    <ProductAmount>{product.quantity}</ProductAmount>
                    <Remove />
                  </ProductAmountContainer>
                  <ProductPrice>$ {product.price}</ProductPrice>
                </PriceDetails>
              </Product>
            ))}
            <Hr />
          </Info>
          <Summary>
            <SummaryTitle>ORDER SUMMARY</SummaryTitle>
            <SummaryItem>
              <SummaryItemText>Subtotal</SummaryItemText>
              <SummaryItemPrice>$ 21.98</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
              <SummaryItemText>Estimated Shipping</SummaryItemText>
              <SummaryItemPrice>$ 5.90</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem>
              <SummaryItemText>Shipping Discount</SummaryItemText>
              <SummaryItemPrice>$ -5.90</SummaryItemPrice>
            </SummaryItem>
            <SummaryItem type='total'>
              <SummaryItemText>Total</SummaryItemText>
              <SummaryItemPrice>$ 21.99</SummaryItemPrice>
            </SummaryItem>
            <Button>CHECK OUT NOW!</Button>
          </Summary>
        </Buttom>
      </Wrapper>
      <Footer />
    </Container>
  )
}

export default Cart
