        //http://localhost:3000/?payment_intent=pi_3KvjCMGEf5WGtFgK1neOd39n&payment_intent_client_secret=pi_3KvjCMGEf5WGtFgK1neOd39n_secret_NfKCME01fiV3vCX0aox2j4JNa&redirect_status=succeeded
        const { error } = await stripe.confirmPayment({
            elements,
            redirect: "if_required"
            // confirmParams: {
            //     // Make sure to change this to your payment completion page
            //     return_url: "http://localhost:3000",
            // },
        });

        // This point will only be reached if there is an immediate error when
        // confirming the payment. Otherwise, your customer will be redirected to
        // your `return_url`. For some payment methods like iDEAL, your customer will
        // be redirected to an intermediate site first to authorize the payment, then
        // redirected to the `return_url`.
        if (error == undefined) {
            setMessage(successMessage);
            onPaymentSuccess()
        } else {
            if (error?.type === "card_error" || error?.type === "validation_error") {
                setMessage(error.message + "");
            } else {
                setMessage("An unexpected error occured.");
            }
        }

        setIsLoading(false);
    }

    return (
        <div>
            <form id="payment-form" onSubmit={handleSubmit}>
                <>
                    <PaymentElement id="payment-element" />
                    <button disabled={isLoading || !stripe || !elements} id="submit">
                        <span id="button-text">
                            {isLoading ? <div className="spinner" id="spinner"></div> : "Pay now"}
                        </span>
                    </button>
                </>
                {/* Show any error or success messages */}
                {message && <div id="payment-message"><p>{message}</p></div>}
            </form>
        </div>
    )
}
import React from "react";
import { Stripe } from "@stripe/stripe-js";
import { Elements } from "@stripe/react-stripe-js";
import CheckoutForm from "./CheckoutForm";

//const stripePromise = loadStripe(import.meta.env.VITE_APP_STRIPE_PK + '');

interface iPayNow {
    title?: string
    successMessage?: string
    stripe: Promise<Stripe | null>
    clientSecret: string
    onClick: () => void
    onPaymentSuccess: () => void
}
export default function PayNow({
    title = "Pay With Stripe", stripe, clientSecret = "", onClick = () => { }, onPaymentSuccess, successMessage = "Payment Successfull"
}: iPayNow) {


    return (
        <div className="rs">
            {!clientSecret && <button type="button" onClick={() => {
                //do your validation before payment
                onClick()
            }}>{title}</button>}
            {clientSecret && (
                <Elements
                    options={{
                        clientSecret,
                        appearance: {
                            theme: "stripe"
                        },
                    }}
                    stripe={stripe}
                >
                    <CheckoutForm onPaymentSuccess={onPaymentSuccess} successMessage={successMessage} />
                </Elements>
            )}
        </div>
    )
}
