import React from 'react'
import styled from 'styled-components'
import Navbar from '../components/Navbar'
import Announcement from '../components/Announcement'
import Footer from '../components/Footer'

const Container = styled.div``

const Wrapper = styled.h1`
  padding: 20px;
`

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  border: 2px solid #ce46e2;
  cursor: pointer;
  border: ${(props) => props.type === 'filled' && 'none'};
  background-color: ${(props) =>
    props.type === 'filled' ? 'wheat' : 'transparent'};
  color: ${(props) => props.type === 'filled' && 'black'};
`

const TopTexts = styled.div``

const TopText = styled.span`
  text-decoration: underline;
  font-size: 25px;
  cursor: pointer;
  margin: 0px 10px;
`

const Buttom = styled.div`
  display: flex;
  justify-content: space-between;
`

const Info = styled.div`
  flex: 3;
`

const Summary = styled.div`
  flex: 1;
`

const Cart = () => {
  return (
    <Container>
      <Announcement />
      <Navbar />
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag(2)</TopText>
            <TopText>WishList(0)</TopText>
          </TopTexts>
          <TopButton type='filled'>CHECKOUT NOW</TopButton>
        </Top>
        <Buttom>
          <Info>
            <Product>
              <ProductDetails>
                <Image src='https://target.scene7.com/is/image/Target/GUEST_7e134dcc-08f9-4337-9726-0cd893e9cddf?wid=315&hei=315&qlt=60&fmt=pjpeg' />
                <Details>
                  <ProductName>
                    <b>Product:</b> Girl`s Hoodie
                  </ProductName>
                  <ProductId>
                    <b>ID:</b> 93813718293
                  </ProductId>
                </Details>
              </ProductDetails>
              <PriceDetails></PriceDetails>
            </Product>
          </Info>
          <Summary>SUMMARY</Summary>
        </Buttom>
      </Wrapper>
      <Footer />
    </Container>
  )
}

export default Cart
