import React from 'react'
import styled from 'styled-components'
import Navbar from '../components/Navbar'
import Announcement from '../components/Announcement'
import Footer from '../components/Footer'
import { Add, Remove } from '@mui/icons-material'
import { mobile } from '../responseive'

const Container = styled.div``

const Wrapper = styled.h1`
  padding: 20px;
  ${mobile({ padding: '10px' })}
`

const Title = styled.h1`
  font-weight: 300;
  text-align: center;
`

const Top = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 20px;
`
const TopButton = styled.button`
  padding: 10px;
  font-weight: 600;
  border: 2px solid #ce46e2;
  cursor: pointer;
  border: ${(props) => props.type === 'filled' && 'none'};
  background-color: ${(props) =>
    props.type === 'filled' ? 'wheat' : 'transparent'};
  color: ${(props) => props.type === 'filled' && 'black'};
`

const TopTexts = styled.div`
  ${mobile({ display: 'none' })}
`

const TopText = styled.span`
  text-decoration: underline;
  font-size: 25px;
  cursor: pointer;
  margin: 0px 10px;
`

const Buttom = styled.div`
  display: flex;
  justify-content: space-between;
  ${mobile({ flexDirection: 'column' })}
`

const Info = styled.div`
  flex: 3;
`
const Product = styled.div`
  display: flex;
  margin-bottom: 15px;
  justify-content: space-between;
`

const ProductDetails = styled.div`
  flex: 2;
  display: flex;
`

const Image = styled.img``

const Details = styled.div`
  padding: 20px;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
`

const ProductName = styled.span`
  font-weight: 300;
`

const ProductId = styled.span`
  font-weight: 300;
`

const ProductColor = styled.span`
  width: 20px;
  height: 20px;
  border-radius: 50%;
  background-color: ${(props) => props.color};
`

const ProductSize = styled.span`
  font-weight: 300;
`

const PriceDetails = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
`

const ProductAmountContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`
const ProductAmount = styled.div`
  margin: 15px;
  font-weight: 300;
`
const ProductPrice = styled.div`
  font-weight: 300;
`

const Hr = styled.hr`
  background-color: #eee;
  border: none;
  height: 1px;
  margin-bottom: 15px;
  margin-right: 15px;
`

const Summary = styled.div`
  flex: 1;
  border: 0.5px solid lightgray;
  border-radius: 10px;
  padding: 20px;
  height: 50vh;
  background-color: whitesmoke;
`
const SummaryTitle = styled.h1`
  font-weight: 200;
  font-size: 50px;
`
const SummaryItem = styled.div`
  margin: 30px 0px;
  display: flex;
  justify-content: space-between;
  font-weight: ${(props) => props.type === 'total' && '800'};
  font-size: ${(props) => props.type === 'total' && '40px'};
`
const SummaryItemText = styled.span`
  font-weight: 200;
`
const SummaryItemPrice = styled.span`
  font-weight: 200;
`
const Button = styled.button`
  width: 100%;
  padding: 10px;
  background-color: wheat;
  color: black;
  font-weight: 600;
  border: none;
  cursor: pointer;
  margin-top: 25px;
`
const Cart = () => {
  return (
    <Container>
      <Announcement />
      <Navbar />
      <Wrapper>
        <Title>YOUR BAG</Title>
        <Top>
          <TopButton>CONTINUE SHOPPING</TopButton>
          <TopTexts>
            <TopText>Shopping Bag(2)</TopText>
            <TopText>WishList(0)</TopText>
          </TopTexts>
          <TopButton type='filled'>CHECKOUT NOW!</TopButton>
        </Top>
        <Buttom>
          <Info>
            <Product>
              <ProductDetails>
                <Image src='https://target.scene7.com/is/image/Target/GUEST_7e134dcc-08f9-4337-9726-0cd893e9cddf?wid=315&hei=315&qlt=60&fmt=pjpeg' />
                <Details>
                  <ProductName>
                    <b>Product:</b> Hoodie
                  </ProductName>
                  <ProductId>
                    <b>ID:</b> 93813718293
                  </ProductId>
                  <ProductColor color='pink' />
                  <ProductSize>
                    <b>Size:</b> M
                  </ProductSize>
                </Details>
              </ProductDetails>
              <PriceDetails>
                <ProductAmountContainer>
                  <Add />
                  <ProductAmount>2</ProductAmount>
                  <Remove />
                </ProductAmountContainer>
                <ProductPrice>$ 10.99</ProductPrice>
              </PriceDetails>
            </Product>
            <Hr />
            <Product>
              <ProductDetails>
                <Image src='https://target.scene7.com/is/image/Target/GUEST_42b0d145-6e15-4af3-948f-2639ee3b6a13?wid=315&hei=315&qlt=60&fmt=pjpeg' />
                <Details>
                  <ProductName>
