import React from 'react'
import styled, { isStyledComponent, StyleSheetManager } from 'styled-components'
import ArrowLeftOutlinedIcon from '@material-ui/icons/ArrowLeftOutlined'
import ArrowRightOutlinedIcon from '@material-ui/icons/ArrowRightOutlined'

const Container = styled.div`
  width: 100%;
  height: 100vh;
  display: flex;
  background-color: #dad2cb;
  position: relative;
  overflow: hidden;
`
const Arrow = styled.div`
  width: 50px;
  height: 50px;
  background-color: whitesmoke;
  border-radius: 50%;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  top: 0;
  bottom: 0;
  left: ${(props) => props.direction === 'left' && '10px'};
  right: ${(props) => props.direction === 'right' && '10px'};
  margin: auto;
  cursor: pointer;
  opacity: 0.5;
`

const Wrapper = styled.div`
  height: 100%;
  display: flex;
`

const Slide = styled.div`
  width: 100vw;
  height: 100vh;
  display: flex;
  align-items: center;
`
const ImgContainer = styled.div`
  height: 650px;
  padding-left: 100px;
  flex: 1;
`
const Image = styled.img`
  height: auto;
`

const InforContainer = styled.div`
  flex: 1;
  padding: 50px;
`
const Title = styled.h1`
  font-size: 70px;
`
const Desc = styled.p`
  margin: 50px 0px;
  font-size: 20px;
  font-weight: 500;
  letter-spacing: 3px;
`
const Button = styled.button`
  padding: 10px;
  //background-color: transparent;
  cursor: pointer;
`

const Slider = () => {
  return (
    <Container>
      <Arrow direction='left'>
        <ArrowLeftOutlinedIcon />
      </Arrow>
      <Wrapper>
        <Slide>
          <ImgContainer>
            <Image src='https://pas-wordpress-media.s3.amazonaws.com/content/uploads/2018/04/19150705/clothing-line.jpg' />
          </ImgContainer>
          <InforContainer>
            <Title>SUMMER SALE</Title>
            <Desc>
              DON`T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.
            </Desc>
            <Button>SHOW NOW</Button>
          </InforContainer>
        </Slide>
        <Slide>
          <ImgContainer>
            <Image src='https://wallpaperaccess.com/full/2489626.jpg' />
          </ImgContainer>
          <InforContainer>
            <Title>T-SHIRT SALE</Title>
            <Desc>
              DON`T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.
            </Desc>
            <Button>SHOW NOW</Button>
          </InforContainer>
        </Slide>
        <Slide>
          <ImgContainer>
            <Image src='https://media.istockphoto.com/photos/wooden-toys-clothes-and-shoes-on-green-background-picture-id1208543231?k=20&m=1208543231&s=612x612&w=0&h=mJLzPtPpM1-3EdP7xL3l_SnEDD7qchwCfhPrKbZfh7Q=' />
          </ImgContainer>
          <InforContainer>
            <Title>KID`S CLOTHES SALE</Title>
            <Desc>
              DON`T COMPROMISE ON STYLE! GET FLAT 30% OFF FOR NEW ARRIVALS.
            </Desc>
            <Button>SHOW NOW</Button>
          </InforContainer>
        </Slide>
      </Wrapper>
      <Arrow direction='right'>
        <ArrowRightOutlinedIcon />
      </Arrow>
    </Container>
  )
}

export default Slider
